

public class GradeCondetion {
	static int Grade ;
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Grade =60;
		
		if(Grade>90) { 
			System.out.println("A");
		}
		
		else if(Grade >80) {
			System.out.println("B");
		}
		
		else {
			System.out.println("C");
		}
		

	}

}
